import classNames from "classnames"
import { useRef } from "react"
import { InputCheckboxComponent } from "./types"
import { useState } from "react"
import { useEffect } from "react"

export const InputCheckbox: InputCheckboxComponent = ({ id, checked, disabled, onChange }) => {
  const inputId = `RampInputCheckbox-${id}`;

  // console.log("Init");

  var isChecked = checked;

  //console.log("initial state is " + isChecked);

  useEffect(() => {
    console.log("Use Effect triggered isChecked is now: " + isChecked);
  }, [isChecked]);


  // useEffect(() => {
  //   setIsChecked(checked);
  //   console.log("UseEffect hook entered state: " + isChecked);
  // }, [checked]);


  const handleCheckboxChange = (e: React.ChangeEvent<HTMLInputElement>) => {

    const newChecked = e.target.checked;
    isChecked = newChecked;
    console.log("after onchange method status: " + isChecked);
    onChange(newChecked);
    console.log("Handle check box change to status: " + isChecked);
  };

  return (
    <div className="RampInputCheckbox--container" data-testid={inputId}>
      <label
        htmlFor={inputId}
        className={classNames("RampInputCheckbox--label", {
          "RampInputCheckbox--label-checked": isChecked,
          'RampInputCheckbox--label-disabled': disabled,
        })}
      />
      <input
        id={inputId}
        type="checkbox"
        className="RampInputCheckbox--input"
        checked={isChecked}
        disabled = {disabled}
        onChange={handleCheckboxChange}
      />
    </div>
  )
}
