import classNames from "classnames"
import { useRef } from "react"
import { InputCheckboxComponent } from "./types"
import { useState } from "react"
import { useEffect } from "react"
import React from "react"

const InputCheckbox: InputCheckboxComponent = ({ id, checked, disabled, onChange }) => {
  const {current: inputIdRef} = useRef(`RampInputCheckbox-${id}`);
  const inputRef = useRef<HTMLInputElement>(null);

  console.log("Rerendering test");

  const handleCheckboxChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (inputRef.current) {
      const newChecked = inputRef.current.checked;
      onChange(newChecked);
    }
  };

  return (
    <div className="RampInputCheckbox--container" data-testid={inputId}>
      <label
        htmlFor={inputId}
        className={classNames("RampInputCheckbox--label", {
          "RampInputCheckbox--label-checked": checked,
          "RampInputCheckbox--label-disabled": disabled,
        })}
      />
      <input
        ref = {inputRef}
        id={inputIdRef.current}
        type="checkbox"
        className="RampInputCheckbox--input"
        checked={checked}
        disabled={disabled}
        onChange={handleCheckboxChange}
      />
    </div>
  );
};

export default React.memo(InputCheckbox);
